import { useState, useEffect } from "react";
import { useNavigate, useParams, useLocation } from "react-router-dom";

// SVG Icons
const Cassino = ({ active }) => (
  <svg
    width="24"
    height="24"
    viewBox="0 0 24 24"
    xmlns="http://www.w3.org/2000/svg"
    className="-pb-2"
  >
    <g clipPath="url(#clip0_367_521)">
      <path
        d="M16.3333 0H8C7.08333 0 6.33333 0.75 6.33333 1.66667V15C6.33333 15.9167 7.08333 16.6667 8 16.6667H16.3333C17.25 16.6667 18 15.9167 18 15V1.66667C18 0.75 17.25 0 16.3333 0ZM16.3333 15H8V1.66667H16.3333V15ZM3 3.33333V18.3333C3 19.25 3.75 20 4.66667 20H14.6667V18.3333H4.66667V3.33333H3ZM12.1667 5C11.25 5 10.5 5.75 10.5 6.66667C10.5 7 10.5833 7.25 10.75 7.5H10.5C9.58333 7.5 8.83333 8.25 8.83333 9.16667C8.83333 10.0833 9.58333 10.8333 10.5 10.8333C11 10.8333 11.4167 10.5833 11.75 10.25L10.9167 12.5H13.4167L12.5833 10.25C12.9167 10.5833 13.3333 10.8333 13.8333 10.8333C14.75 10.8333 15.5 10.0833 15.5 9.16667C15.5 8.25 14.75 7.5 13.8333 7.5H13.5833C13.75 7.25 13.8333 7 13.8333 6.66667C13.8333 5.75 13.0833 5 12.1667 5Z"
        fill={active ? "#FFFFFF" : "#FFFFFF"}
      />
    </g>
  </svg>
);

const VirtualCassino = ({ active }) => (
  <svg width="24" height="24" viewBox="0 0 24 24" fill="none">
    <path
      d="M12 2L2 7L12 12L22 7L12 2Z"
      stroke={active ? "#FFFFFF" : "#FFFFFF"}
      strokeWidth="2"
      strokeLinecap="round"
      strokeLinejoin="round"
    />
    <path
      d="M2 17L12 22L22 17"
      stroke={active ? "#FFFFFF" : "#FFFFFF"}
      strokeWidth="2"
      strokeLinecap="round"
      strokeLinejoin="round"
    />
    <path
      d="M2 12L12 17L22 12"
      stroke={active ? "#FFFFFF" : "#FFFFFF"}
      strokeWidth="2"
      strokeLinecap="round"
      strokeLinejoin="round"
    />
  </svg>
);

const DragonTiger = ({ active }) => (
  <svg width="24" height="24" viewBox="0 0 24 24" fill="none">
    <path
      d="M12 2C6.48 2 2 6.48 2 12C2 17.52 6.48 22 12 22C17.52 22 22 17.52 22 12C22 6.48 17.52 2 12 2ZM12 20C7.59 20 4 16.41 4 12C4 7.59 7.59 4 12 4C16.41 4 20 7.59 20 12C20 16.41 16.41 20 12 20Z"
      fill={active ? "#FFFFFF" : "#FFFFFF"}
    />
  </svg>
);

export default function GamesOptions({
  withHeader = false,
  data,
  data2,
  onSubCategorySelect,
}) {
  // console.log("category data:", data);
  

  const [active, setActive] = useState(1);
  const [categories, setCategories] = useState([]);
  const navigate = useNavigate();
  const { tabName } = useParams();
  const location = useLocation();

  // Extract unique categories from data
  useEffect(() => {
    if (data && Array.isArray(data)) {
      // Get unique categories from data
      const uniqueCategories = [...new Set(data.map((game) => game.category))];

      console.log("Unique categories:", uniqueCategories);

      // Create category objects with icons
      const categoryList = [
        { id: "see all", label: "See All", type: "text" },
        ...uniqueCategories.map((cat) => ({
          id: cat,
          label: formatCategoryLabel(cat),
          type: "custom",
          icon: getCategoryIcon(cat),
        })),
      ];

      setCategories(categoryList);
    } else {
      // Default categories if no data
      setCategories([
        { id: "see all", label: "See All", type: "text" },
        { id: "casino", label: "Live Casino", type: "custom", icon: Cassino },
        { id: "evolution", label: "Evolution", type: "custom", icon: Cassino },
        {
          id: "virtual",
          label: "Virtual Casino",
          type: "custom",
          icon: VirtualCassino,
        },
        {
          id: "DragonTiger",
          label: "Dragon Tiger",
          type: "custom",
          icon: DragonTiger,
        },
      ]);
    }
  }, [data, tabName]);



  useEffect(() => {
    if (data2 && data2.length > 0) {
      setActive(data2[0].id);
      onSubCategorySelect(data2[0]);
    }
  }, [data2]);


  // Format category label (capitalize first letter, handle special cases)
  const formatCategoryLabel = (category) => {
    if (!category) return "Unknown";

    // Handle special cases
    const specialCases = {
      flash: "Flash Games",
      slot: "Slot Games",
      fish: "Fishing Games",
      chess: "Chess Games",
      CasinoTable: "Casino Table",
    };

    if (specialCases[category]) {
      return specialCases[category];
    }

    // Capitalize first letter
    return category.charAt(0).toUpperCase() + category.slice(1);
  };

  // Get icon for category (you can customize this based on category type)
  const getCategoryIcon = (category) => {
    const iconMap = {
      flash: Cassino,
      slot: VirtualCassino,
      fish: DragonTiger,
      chess: Cassino,
      CasinoTable: Cassino,
      casino: Cassino,
      virtual: VirtualCassino,
    };

    return iconMap[category] || Cassino; // Default to Cassino icon
  };

  // Sync active with URL param if available
  useEffect(() => {
    if (location.pathname.startsWith("/game") && tabName) {
      setActive(tabName);
    }
  }, [tabName, location.pathname]);

  const handleClick = (cat) => {
    console.log("sub cat id:", cat);
    setActive(cat.id); // UI active highlight
    onSubCategorySelect(cat);
  };

  return (
    <div className="w-full overflow-x-auto hide-scrollbar px-0">
      <div className="flex gap-3 min-w-max">
        {data2.map((cat) => {
          const IconComp = cat.icon;
          return (
            <button
              key={cat.id}
              onClick={() => handleClick(cat)}
              className={`flex flex-col items-center justify-center
                w-[95px] h-[66px] px-2 py-1
                rounded-[10px] border text-white
                transition-all duration-200
                ${
                  active === cat.id
                    ? "bg-red border-[#969696]"
                    : "bg-[#969696] border-gray-300"
                }`}
            >
              {/* Render icon only if exists */}
              {/* {cat.icon && <IconComp active={active === cat.id} />} */}
              <img src={cat.image} alt="" className="w-7 h-6" />

              <span
                className="text-center leading-tight mt-1 text-[12px] font-medium truncate w-full"
                title={cat.sub_cat_name}
              >
                {cat.sub_cat_name}
              </span>
            </button>
          );
        })}
      </div>
    </div>
  );
}