export default function SlidingTabs({ withHeader = false }) {
  const [active, setActive] = useState("home");
  const naviagte = useNavigate();
  const { tabName } = useParams(); // works only on /game/:tabName
  const location = useLocation();
  // If on game page, highlight based on URL param
  // const activeTab = location.pathname.startsWith("/game") ? tabName : null;
  // 🔹 Sync "active" with URL param if available
  useEffect(() => {
    if (location.pathname.startsWith("/game") && tabName) {
      setActive(tabName);
    }
  }, [tabName, location.pathname]);

  const handleClick=(id)=>{
setActive(id)
if(id=="home"){
naviagte("/")
}
else{
naviagte(`/game/${id}`);
}

  }
  return (
    <div className="w-full overflow-x-auto hide-scrollbar px-2">
      <div className="flex gap-2 min-w-max">
        {categories.map((cat) => {
          const IconComp = cat.icon;
          return (
            <button
              key={cat.id}
              onClick={() => {
                handleClick(cat.id)
              }}
              className={`flex items-center gap-2 px-4 py-2 rounded-[10px] border 
                transition-all duration-200
                ${
                  active === cat.id
                    ? "bg-[#c8102e] text-white border-[#c8102e]"
                    : "bg-white text-[#c8102e] border-gray-300"
                }`}
            >
              {/* ✅ Render icon only if exists */}
              {cat.icon && <IconComp active={active === cat.id} />}
              <span className="whitespace-nowrap">{cat.label}</span>
            </button>
          );
        })}
      </div>
    </div>
  );
}
